", "fleck", "felon", "gourd", "brown", "thrum", "ficus", "stash", "decry", "wiser", "junta", "visor", "daunt", "scree", "impel", "await", "press", "whose", "turbo", "stoop", "speak", "mangy", "eying", "inlet", "crone", "pulse", "mossy", "staid", "hence", "pinch", "teddy", "sully", "snore", "ripen", "snowy", "attic", "going", "leach", "mouth", "hound", "clump", "tonal", "bigot", "peril", "piece", "blame", "haute", "spied", "undid", "intro", "basal", "shine", "gecko", "rodeo", "guard", "steer", "loamy", "scamp", "scram", "manly", "hello", "vaunt", "organ", "feral", "knock", "extra", "condo", "adapt", "willy", "polka", "rayon", "skirt", "faith", "torso", "match", "mercy", "tepid", "sleek", "riser", "twixt", "peace", "flush", "catty", "login", "eject", "roger", "rival", "untie", "refit", "aorta", "adult", "judge", "rower", "artsy", "rural", "shave"];

	function getBlankLetterFrequency(numLetters=5)	{
		let letterFrequency = {};
		for (let pos=0; pos<numLetters; pos++){
			letterFrequency[pos] = {};
			for (let char=97; char<123; char++){
				letterFrequency[pos][String.fromCharCode(char)] = 0;
			}
		}
		return letterFrequency;
	}

	function getBlankPotentialLetters(numLetters=WORDLENGTH)	{
		let potentialLetters = {};
		for (let pos=0; pos<numLetters; pos++){
			potentialLetters[pos] = [];
			for (let char=97; char<123; char++){
				potentialLetters[pos].push(String.fromCharCode(char));
			}
		}
		return potentialLetters;
	}

	function cleanPotentialLetters(guess, result, potentialLetters){
		for (let pos=0; pos<WORDLENGTH; pos++){
			if (result[pos] === 0){
				for (let _pos=0; _pos<WORDLENGTH; _pos++){
					if (potentialLetters[_pos].includes(guess[pos])){
						potentialLetters[_pos] = potentialLetters[_pos].filter(letter => letter !== guess[pos]);
					}
				}
			} else if (result[pos] === 1){
				potentialLetters[pos] = potentialLetters[pos].filter(letter => letter !== guess[pos]);
			} else if (result[pos] === 2) {
				potentialLetters[pos]=[guess[pos]];
			}
		}
		return potentialLetters;
	}

	function makeGuess(guess, answer) {
		let result = [];
		for (let pos=0; pos<answer.length; pos++) {
			if (guess[pos] === answer[pos]) {
				result.push(2);
			} else if (answer.includes(guess[pos])) {
				result.push(1);
			} else {
				result.push(0);
			}
		}
		return result;
	}

	function sortObject(obj) {
		let sorted = {};
		let keys = Object.keys(obj).sort(function(a, b) { return obj[b] - obj[a] });
		for (let i=0; i<keys.length; i++) {
			sorted[keys[i]] = obj[keys[i]];
		}
		return sorted;
	}

	let relevanceScores = {};
	let potentialLetters = getBlankPotentialLetters();
	let mustContain = [];
	let answer = "tooth";
	let filteredAnswers = wordle_answers;

	function clicker() {
		if (entries[entries.length-1].length < WORDLENGTH)
			return;
		relevanceScores = {};
		let res = makeGuess(entries[entries.length-1], answer);
		console.log(res);
		for (let pos=0; pos<WORDLENGTH; pos++) {
			if (res[pos] != 0 && !mustContain.includes(entries[entries.length-1][pos])) {
				mustContain.push(entries[entries.length-1][pos]);
			}
		}
		potentialLetters = cleanPotentialLetters(entries[entries.length-1], res, potentialLetters);
		let letterFrequency = getBlankLetterFrequency();	
		let toRemove = [];
		filteredAnswers.forEach(word => {
			let remove = false;
			for (let letterPos=0; letterPos<word.length; letterPos++) {
				if (!potentialLetters[letterPos].includes(word[letterPos])) {
					remove = true;
				}
			}
			mustContain.forEach(letter => {
				if (!word.includes(letter)) {
					remove = true;
				}
			});
			if (remove) {
				toRemove.push(word);
			}
		});
		toRemove.forEach(deadWord => {
			filteredAnswers = filteredAnswers.filter(word => word !== deadWord);
		});
		filteredAnswers.forEach(word => {
			for (let letterPos=0; letterPos<word.length; letterPos++) {
				letterFrequency[letterPos][word[letterPos]]++;
			}
		});
		filteredAnswers.forEach(word => {
			relevanceScores[word] = 0;
			for (let letterPos=0; letterPos<word.length; letterPos++) {
				relevanceScores[word] += letterFrequency[letterPos][word[letterPos]];
			}
		});
		relevanceScores = sortObject(relevanceScores);
		states = [...states, [-1,0,1,2,2]];
		if (filteredAnswers.length > 1) {
			entries = [...entries, []];
		}
	};

	let entries=[[]];
	let states=[];

	function handleKeydown(event) {
		let key = event.key;
		let keyCode = event.keyCode;
		if (keyCode == 8) {
			entries[entries.length-1] = entries[entries.length-1].slice(0, -1);
		} else if (keyCode >= 65 && keyCode <= 90 && entries[entries.length-1].length < WORDLENGTH) {
			entries[entries.length-1] = [...entries[entries.length-1], key];
		} else if (keyCode === 13 && entries[entries.length-1].length === WORDLENGTH) {
			clicker();
		}
	}

	function tilePress(e_index, l_index) {
		alert("tile pressed! " + e_index + "," + l_index);
	}
	
	function getTileStateName(state) {
		console.log(state);
		if (state === 0) {
			return "absent";
		} else if (state === 1) {
			return "present";
		} else if (state === 2) {
			return "correct";
		} else if (state === -1) {
			return "tbd";
		}
		return "empty";
	}

	function getTileState(e_index, l_index) {
		if (e_index >= states.length) {
			return -1;
		}
		return states[e_index][l_index];
	}

</script>

<svelte:window on:keydown={handleKeydown}/>
<div id="board-container">
	<div id="board" style="width: 350px; height: 420px;">
		{#each entries as entry, e_index}
			<game-row length={WORDLENGTH}>
				<div class="row">
					{#each entry as letter, l_index}
						<game-tile on:click={() => tilePress(e_index, l_index)}>
							<div class="tile" data-state="{getTileStateName(getTileState(e_index, l_index))}">{letter}</div>
						</game-tile>
					{/each}
					{#each {length: WORDLENGTH - entry.length} as _}
						<game-tile>
							<div class="tile" data-state="empty"></div>
						</game-tile>
					{/each}
				</div>
			</game-row>
		{/each}
	</div>
</div>

<button on:click="{clicker}">Click me</button>

{#each Object.entries(relevanceScores) as [word, score]}
	<p>{word}: {score}</p>
{/each}

